sp_widget.client_script:
function($scope, $window) {
  /* widget controller */
  var c = this;


	c.groupChange = function(){
		c.data.selected = c.data.group;
		c.server.update();
	}

	c.open = function(id){
	var h = screen.height - 200;
  var w = screen.width - 900;
	var url = '/change_request.do?sys_id=' + id;
  var myWindow = window.open(url, "", "resizable=1,width=" + w +",height=" + h);

	}
	c.reviewAll = function(){
		c.data.reviewAll = true;
		c.server.update();
	}

	c.reviewChange = function(id){
		c.data.changeID = id;
		c.server.update();
	}

	c.removeReview = function(id){
		c.data.changeIDr = id;
		c.server.update();
	}

	c.concernChange = function(id){
		c.data.concern = id;
		c.server.update();
	}

	c.concernRemove = function(id){
		c.data.concernR = id;
		c.server.update();
	}

	$scope.reviewed = function(id){
		var array = c.data.reviewJSON;
		var result = array.indexOf(id);
		if(result > 1)
			return true;
		return false;
	}

	$scope.concerns = function(id){
		var array = c.data.concernJSON;
		var result = array.indexOf(id);
		if(result > 1)
			return true;
		return false;
	}



	c.sw = function(sw){
		c.server.update();

	}

	c.setDefault = function(id){
		c.data.setDefault = id;
		c.server.update();

	}



}

