sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var DefaultGroupName = "change_review_group_default";
	var ch = new x_sthc_change_revi.Change_Review();

	if(!input){
	var defaultGroup = getDefaultGroup(gs.getUserID(),DefaultGroupName);
		data.group = defaultGroup.toString();
		data.selectedGroup = defaultGroup.toString();

	data.changes = [];
	data.changes = ch.getAppList();
	data.review = [];
  data.review = ch.getReviewList(data.selectedGroup);
	data.reviewJSON = JSON.stringify(data.review);

	data.fullReview = [];
	//data.fullReview = ch.getFullReviewList();

	data.concernArr = [];
	data.concernArr = ch.getConcernList();
	data.concernJSON = JSON.stringify(data.concernArr);

		data.groups = [];
		//data.groups = getGroupsArray(gs.getUserID());
		data.groups = ch.getGroupsArray();


	}

	if(input){
		if(input.selected){
			data.review = ch.getReviewList(input.group);
	    data.reviewJSON = JSON.stringify(data.review);
		}
		if(input.reviewAll == true){
			var cr = new GlideRecord('change_request');
			cr.addEncodedQuery('stateIN10,12,9^type=Normal^ORtype=normal^cmdb_ci.sys_class_name=cmdb_ci_appl^ORcmdb_ci.sys_class_name=u_application_modules^ORcmdb_ci.sys_class_name=u_cmdb_ci_epic_ini');
			cr.query();
			while(cr.next()){
				reviewChange(cr.sys_id,gs.getUserID());
			}
			data.review = ch.getReviewList(input.group);
	    data.reviewJSON = JSON.stringify(data.review);
			input.reviewAll = '';
			data.reviewAll = '';

		}

		if((input.changeID != '')&&(input.changeID)){
			reviewChange(input.changeID,gs.getUserID(),input.group);
			data.review = ch.getReviewList(input.group);
	    data.reviewJSON = JSON.stringify(data.review);
			input.changeID = '';
			data.changeID = '';
		}

			if((input.changeIDr != '')&&(input.changeIDr)){
			removeReview(input.changeIDr,gs.getUserID(),input.group);
			data.review = ch.getReviewList(input.group);
	    data.reviewJSON = JSON.stringify(data.review);
			input.changeIDr = '';
			data.changeIDr = '';
		}

		if((input.concern != '')&&(input.concern)){
			concernChange(input.concern,gs.getUserID(),input.group,ch);
	    data.concernArr = ch.getConcernList();
	    data.concernJSON = JSON.stringify(data.concernArr);
			input.concern = '';
			data.concern = '';
		}

		if((input.concernR != '')&&(input.concernR)){
			removeConcern(input.concernR,gs.getUserID());
	    data.concernArr = ch.getConcernList();
	    data.concernJSON = JSON.stringify(data.concernArr);
			input.concernR = '';
			data.concernR = '';
		}
		if((input.setDefault != '')&&(input.setDefault)){
			if(checkExisiting(gs.getUserID(),DefaultGroupName,input.group) == true){
				updateDefault(gs.getUserID(),DefaultGroupName,input.group);
				gs.addInfoMessage("Your default group has been updated");
				input.group = '';
			}
			else{
				createDefault(gs.getUserID(),DefaultGroupName,input.group);
				gs.addInfoMessage("Your default group has been set");
			}
		}


	}
})();

function reviewChange(id,user,group){
	var gr = new GlideRecord('x_sthc_change_revi_change_review_table');
	gr.initialize();
	gr.setValue('change',id);
	gr.setValue('user',user);
	gr.setValue('groups', group);
	gr.insert();


}

function removeReview(id,user,group){
	var gr = new GlideRecord('x_sthc_change_revi_change_review_table');
	gr.addQuery('change.sys_id', id);
	gr.addQuery('user.sys_id',user);
	gr.addQuery('groups.sys_id',group);
	gr.query();
	while(gr.next()){
		gr.deleteRecord();
	}

}

function concernChange(id,user,group,ch){
	var gr = new GlideRecord('x_sthc_change_revi_change_review_table');
	gr.addQuery('change.sys_id',id);
	gr.addQuery('user.sys_id',user);
	gr.query();
	if(gr.next()){
		gr.setValue('concern',true);
		gr.update();
	}
	else{ 
		reviewChange(id,user,group);
		concernChange(id,user);
		data.review = ch.getReviewList();
	    data.reviewJSON = JSON.stringify(data.review);
	}
}

function removeConcern(id,user){
	var gr = new GlideRecord('x_sthc_change_revi_change_review_table');
	gr.addQuery('change.sys_id',id);
	gr.addQuery('user.sys_id',user);
	gr.query();
	if(gr.next()){
		gr.setValue('concern',false);
		gr.update();
	}

}

function getGroups(user,group){
	var list = '';
	var gr = new GlideRecord('sys_user_grmember');
	if(group){
		gr.addQuery('sys_id',group);
	}
	gr.addQuery('user', user);
	gr.addQuery('group.u_assign',true);
	gr.addQuery('group.active',true);
	gr.addQuery('group.type','CONTAINS','63345729138ef6847dc7d3228144b0b0');
	gr.query();
	while(gr.next()){
		list += "," + gr.getValue('group');

	}
	return list;

}

function checkExisiting(userID,name,groupID){
	var gr = new GlideRecord('sys_user_preference');
	gr.addQuery('user',userID);
	gr.addQuery('name',name);
	gr.query();
	if(gr.next()){
		gr.value = groupID;
		gr.update();
		return true;
	}
	else
		return false;
}

function createDefault(userID,name,groupID){
	var gr = new GlideRecord('sys_user_preference');
	gr.initialize();
	gr.setValue('name',name);
	gr.setValue('user',userID);
	gr.setValue('value',groupID);
	gr.insert();	
}

function updateDefault(userID,name,groupID){
  var gr = new GlideRecord('sys_user_preference');
	gr.addQuery('user',userID);
	gr.addQuery('name',name);
	gr.query();
	if(gr.next()){
		gr.value = groupID;
	 gr.update();
	}

}

function getDefaultGroup(userID,name){
	var gr = new GlideRecord('sys_user_preference');
	gr.addQuery('user',userID);
	gr.addQuery('name',name);
	gr.query();
	if(gr.next())
		return gr.value;
	else
		return '';

}
